<div class="language-bash ext-sh line-numbers-mode"><pre v-pre class="language-bash"><code><span class="token shebang important">#!/bin/bash</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable"><span class="token environment constant">LANG</span></span><span class="token operator">=</span><span class="token string">"en_US.UTF-8"</span>

<span class="token assign-left variable">arr_hostname</span><span class="token operator">=</span><span class="token punctuation">(</span><span class="token string">"192.168.165.115"</span> <span class="token string">"192.168.9.13"</span><span class="token punctuation">)</span>
<span class="token assign-left variable">len</span><span class="token operator">=</span><span class="token variable">${<span class="token operator">#</span>arr_hostname<span class="token punctuation">[</span>@<span class="token punctuation">]</span>}</span>
<span class="token assign-left variable">dir</span><span class="token operator">=</span><span class="token variable"><span class="token variable">$(</span><span class="token builtin class-name">cd</span> <span class="token punctuation">$(</span>dirname $0<span class="token punctuation">)</span> <span class="token operator">&amp;&amp;</span> <span class="token builtin class-name">pwd</span><span class="token variable">)</span></span>
<span class="token assign-left variable">tdir</span><span class="token operator">=</span><span class="token string">"<span class="token variable">$dir</span>/tmp"</span>

<span class="token assign-left variable">dt</span><span class="token operator">=</span><span class="token variable"><span class="token variable">`</span><span class="token function">date</span> <span class="token string">"+%F %T"</span><span class="token variable">`</span></span>

<span class="token punctuation">[</span> -f <span class="token variable">$tdir</span>/code_error.txt <span class="token punctuation">]</span> <span class="token operator">&amp;&amp;</span> <span class="token boolean">true</span> <span class="token operator">></span><span class="token variable">$tdir</span>/code_error.txt

<span class="token comment">## i: 项目信息   j: url   k:时间间隔</span>
<span class="token keyword">while</span> <span class="token builtin class-name">read</span> i j k o<span class="token punctuation">;</span><span class="token keyword">do</span>
    <span class="token keyword">if</span> <span class="token punctuation">[</span><span class="token punctuation">[</span> <span class="token operator">!</span> x<span class="token string">"<span class="token variable">$o</span>"</span> <span class="token operator">==</span> x<span class="token string">""</span> <span class="token operator">&amp;&amp;</span> <span class="token variable">$o</span> -le <span class="token variable"><span class="token variable">$((</span>$len<span class="token operator">-</span><span class="token number">1</span><span class="token variable">))</span></span> <span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span><span class="token keyword">then</span>
        <span class="token function">curl</span> -s -I <span class="token string">"<span class="token variable">$j</span>"</span> <span class="token operator">></span> <span class="token variable">$tdir</span>/curl.txt
        <span class="token assign-left variable">code</span><span class="token operator">=</span><span class="token variable"><span class="token variable">`</span><span class="token function">grep</span> <span class="token string">'HTTP/1.1'</span> $tdir/curl.txt<span class="token operator">|</span><span class="token function">awk</span> <span class="token string">'{print $2}'</span><span class="token variable">`</span></span>
        <span class="token comment">#echo "$i $j $code" </span>

        <span class="token keyword">if</span> <span class="token punctuation">[</span> <span class="token variable">$code</span> -eq <span class="token number">200</span> -o <span class="token variable">$code</span> -eq <span class="token number">301</span> -o <span class="token variable">$code</span> -eq <span class="token number">302</span> -o <span class="token variable">$code</span> -eq <span class="token number">405</span> <span class="token punctuation">]</span><span class="token punctuation">;</span><span class="token keyword">then</span>
            python <span class="token variable">$dir</span>/zabbix_agent.py <span class="token variable">$i</span> <span class="token variable">$j</span> <span class="token variable">$k</span> <span class="token variable">${arr_hostname<span class="token punctuation">[</span>$o<span class="token punctuation">]</span>}</span>
            <span class="token punctuation">[</span> <span class="token variable">$?</span> -eq <span class="token number">0</span> <span class="token punctuation">]</span> <span class="token operator">&amp;&amp;</span> <span class="token builtin class-name">echo</span> <span class="token string">"<span class="token variable">$dt</span> <span class="token variable">$i</span> <span class="token variable">$j</span> <span class="token variable">$k</span> <span class="token variable">$o</span> create ok"</span> <span class="token operator">>></span> <span class="token variable">$tdir</span>/info <span class="token operator">||</span> <span class="token builtin class-name">echo</span> <span class="token string">"<span class="token variable">$dt</span> <span class="token variable">$i</span> <span class="token variable">$j</span> <span class="token variable">$k</span> <span class="token variable">$o</span> create fail"</span> <span class="token operator">>></span><span class="token variable">$tdir</span>/info
        <span class="token keyword">else</span>
            <span class="token builtin class-name">echo</span> <span class="token string">"<span class="token variable">$i</span> <span class="token variable">$j</span> <span class="token variable">$k</span> <span class="token variable">$o</span> <span class="token variable">$code</span>"</span> <span class="token operator">>></span><span class="token variable">$tdir</span>/code_error.txt
            <span class="token builtin class-name">echo</span> <span class="token string">"<span class="token variable">$i</span> <span class="token variable">$code</span>"</span>
        <span class="token keyword">fi</span>
    <span class="token keyword">else</span>
        <span class="token builtin class-name">echo</span> <span class="token string">"hostname参数传递错误"</span>
        <span class="token keyword">fi</span>
<span class="token keyword">done</span> <span class="token operator">&lt;</span><span class="token variable">$dir</span>/list
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br></div></div><p><code>list</code>文件内容格式如下：</p>
